mapper.select:
SELECT categoryno, name, namesub, cnt, rdate, seqno, visible
FROM category
WHERE categoryno=#{categoryno}

mapper.update:
UPDATE category
SET name=#{name}, namesub=#{namesub}, cnt=#{cnt}, rdate=sysdate, seqno=#{seqno}, visible=#{visible}
WHERE categoryno=#{categoryno}

UPDATE category
SET seqno= seqno -1
WHERE categoryno = #{categoryno}

UPDATE category
SET seqno= seqno +1
WHERE categoryno = #{categoryno}

mapper.select:
SELECT categoryno, name, namesub, cnt, rdate, seqno, visible, r FROM ( SELECT categoryno, name, namesub, cnt, rdate, seqno, visible, rownum as r FROM ( SELECT categoryno, name, namesub, cnt, rdate, seqno, visible FROM category
WHERE UPPER(name) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(namesub) LIKE '%' || UPPER(#{word}) || '%'
ORDER BY seqno ASC ) ) WHERE r > = #{start_num} AND r < = #{end_num}

mapper.update:
UPDATE category
SET cnt = cnt + 1
WHERE categoryno = #{categoryno}

UPDATE category
SET cnt = cnt - 1
WHERE categoryno = #{categoryno}
